import { useState } from "react";


function ContactUs() {
  const [ value, setValue ] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    reqText: "",
    reqCode: "requireUs",
  });
  const contactUsText = (data) => {
    console.log("data1", data);
    fetch("/api/contactUs", {
      method: "POST",
      body: JSON.stringify({
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
        reqText: "",
        reqCode: "requireUs",

      }),
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded; charset=utf-8'
      }
    })
    .then((res) => res.json())
    .then((data) => console.log("data2", data));
  };



  return (
    <div className="contactUs">
      {/* <form className="contactUsForm" action="/api/contactUs" method="post"> */}
      <form className="contactUsForm">
        <div>
        <p>*</p><label>姓名：</label>
        <input type="text" name="firstName" placeholder="名字" required={true} onChange={(e) => e.target.value}></input>
        <input type="text" name="lastName" placeholder="姓氏" required={true}></input>
        </div>

        <div>
        <p>*</p><label>Email:</label>
        <input type="email" name="email" placeholder="電子信箱" required={true}></input>          
        </div>

        <div>
        <p>*</p><label>電話:</label>
        <input type="tel" name="phone" placeholder="電話" required={true}></input>          
        </div>

        <div>
        <p>*</p><label>內容:</label>
        <textarea name="reqText" placeholder="內容..." required={true}></textarea>          
        </div>
        <div>

        <input type="hidden" name="reqCode" readOnly={true} value="requireUs"></input>
        </div>

        <div>
          <input type="submit" name="submit" value="提交" onClick={contactUsText}></input>
        </div>

      </form>
    </div>
  );
};

export default ContactUs;
