import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher2() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div className="teacherCard">
              <h5 className="teacherName">{teacher[1].name}</h5>
        <h5 className="teacherInstrument">{teacher[1].instrument}</h5>
      <div className="smImgDiv">
        <img 
          id='base64image' 
          className="base64image"
          src={'data:image/jpeg;base64,' + teacher[1].image}
          style={{"width": "200px"}}
          onClick={() => {
          setBigImg(true);
        }}/>
      </div>
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
            className="bigImg"
            src={'data:image/jpeg;base64,' + teacher[1].image}
          />
          <div 
            className="close" 
            onClick={() => {
            setBigImg(false);
          }}>
            X
          </div>
        </div>
         : <></>
      }
      <div className="teacherMessage">
        {/* <h5 className="teacherName">{teacher[1].name}</h5>
        <h5 className="teacherInstrument">{teacher[1].instrument}</h5> */}
        <p className="teacherIntroduction">{teacher[1].introduction}</p>        
      </div>
    </div>
  );
};

export default Teacher2;
