import { Link } from "react-router-dom";
import { useContext } from "react";
import { UserContext } from "../context";

function NavTeacher() {
  const instrumentValue = [];
  const teacher = useContext(UserContext);
  const instrument = teacher.map((data) => {
    if (instrumentValue.indexOf(data.instrument) === -1) {
      instrumentValue.push(data.instrument);
    }
  });
  console.log("instrument:", instrumentValue)

  const violin = teacher.map((data, index) => {
    if (data.instrument === "小提琴") {
      return (
        <li key={index}>
          <Link to={`/${data._id}`} className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
    return [];
  });

  const viola = teacher.map((data, index) => {
    if (data.instrument === "中提琴") {
      return (
        <li key={index}>
          <Link to={`/${data._id}`} className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
    return [];
  });

  const cello = teacher.map((data, index) => {
    if (data.instrument === "大提琴") {
      return (
        <li key={index}>
          <Link to={`/${data._id}`} className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
    return [];
  });

  const piano = teacher.map((data, index) => {
    if (data.instrument === "鋼琴") {
      return (
        <li key={index}>
          <Link to={`/${data._id}`} className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
    return [];
  });

  const flute = teacher.map((data, index) => {
    if (data.instrument === "長笛") {
      return (
        <li key={index}>
          <Link to={`/${data._id}`} className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
    return [];
  });

  return (
    <ul className="instrumentUl">

      <li className="instrumentLi">
        <Link className="link instrument violin">小提琴</Link>
        <ul className="navTeacherUl">
        {violin}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link className="link instrument viola">中提琴</Link>
        <ul className="navTeacherUl">
          {viola}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link className="link instrument cello">大提琴</Link>
        <ul className="navTeacherUl">
          {cello}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link className="link instrument piano">鋼琴</Link>
        <ul className="navTeacherUl">
          {piano}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link className="link instrument flute">長笛</Link>
        <ul className="navTeacherUl">
          {flute}
        </ul>
      </li>

    </ul>
  );
};

export default NavTeacher;
