import { useState } from "react";

function InquireEdit() {
  const [ id, setId ] = useState("");
  const [ state, setState ] = useState("");
  const [ inquire, setInquire ] = useState({
    date: "",
    name: "",
    email: "",
    birthday: "",
    addressCounty: "",
    addressDistrict: "",
    zipCode: "",
    address: "",
    id: "",
    roman: "",
    school: "",
    grade: "",
    graded: "",
    phone: "",
    fatherName: "",
    fatherPhone: "",
    matherName: "",
    matherPhone: "",
    studentPhone: "",
    introducer: "",
    instrument: "",
    experience: "",
    track: "",
    specify: "",
    friend1: "",
    friend2: "",
    friend3: "",
    diseaseVerify: "",
    disease: "",
    food: "",
    height: "",
    weight: "",
    TShirtSize: "",
  });

  const inquireText = async () => {
    fetch("/api/signUp/inquire/" + id)
    .then((res) => {return res.json()})
    .then((data) => {
      console.log(data)
      setState(data);
      console.log(1)

      setInquire((prev) => {
        return {
          ...prev,
          date: state[0].date,
        }
      });
    })
    .catch((err) => console.log("error:", err));
  }
  const idText = (e) => {
    setId((prev) => e.target.value)
  }
  console.log(state)
  console.log(inquire)

  // const inquireForm = (
  //   <from>
  //     <div>{state[0].date}</div>
  //   </from>
  // )

  return (
    <div className="inquireEdit">
      <h2><i className="bi bi-search"></i> 查詢報名資料</h2>
      <form className="inquireEditForm">

      <label><h4>身分證字號: </h4></label>
      <input type="text" name="id" maxLength="10" value={id} onChange={(e) => idText(e)} required={true} />

      <input className="inputText" type="submit" name="submit" value="提交" onClick={inquireText} />
      {
        state === "" ?
        <div></div>
        :
        // inquireForm
        <div>
          <label><h5>申請日期: </h5></label>
          <div type="text" readOnly={true} >{state[0].date}</div>
          姓名: <input type="text" disabled={true} value={state[0].name} />
        </div>


      }

      </form>
    </div>
  );
};

export default InquireEdit;
