import { useState, useContext } from "react";
import { UserContext } from "../context";


function Teacher11() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div>
      <div>{teacher[10].name}</div>
      <div>{teacher[10].instrument}      
        <img 
          id='base64image' 
          className="base64image"
          src={'data:image/jpeg;base64,' + teacher[10].image}
          style={{"width": "200px"}}
          onClick={() => {
            setBigImg(true);
        }}/>
      </div>
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
            className="bigImg"
            src={'data:image/jpeg;base64,' + teacher[10].image}
          />
          <div className="close" 
            onClick={() => {
            setBigImg(false);
          }}>
            X
          </div>
        </div>
         : <></>
      }
      <div className="teacherMessage">
        <h5 className="teacherName">{teacher[10].name}</h5>
        <h5 className="teacherInstrument">{teacher[10].instrument}</h5>
        <p className="teacherIntroduction">{teacher[10].introduction}</p>
      </div>
    </div>
  );
};

export default Teacher11;
