import { Link } from "react-router-dom";
import { useContext } from "react";
import { UserContext } from "../context";

function NavTeacher() {
  const teacher = useContext(UserContext);

  const violin = teacher.map((data, index) => {
    if (data.instrument === "小提琴") {
      return (
        <li key={index}>
          <Link to={`/${index}`} className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
  });

  const viola = teacher.map((data, index) => {
    if (data.instrument === "中提琴") {
      return (
        <li key={index}>
          <Link to="/" className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
  });

  const cello = teacher.map((data, index) => {
    if (data.instrument === "大提琴") {
      return (
        <li key={index}>
          <Link to="/" className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
  });

  const piano = teacher.map((data, index) => {
    if (data.instrument === "鋼琴") {
      return (
        <li key={index}>
          <Link to="/" className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
  });

  const flute = teacher.map((data, index) => {
    if (data.instrument === "長笛") {
      return (
        <li key={index}>
          <Link to="/" className="link navTeacher">{data.name}</Link>
        </li>
      );
    };
  });

  return (
    <ul className="instrumentUl">

      <li className="instrumentLi">
        <Link to="/Teacher" className="link instrument violin">小提琴</Link>
        <ul className="navTeacherUl">
        {violin}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link to="/Teacher" className="link instrument viola">中提琴</Link>
        <ul className="navTeacherUl">
          {viola}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link to="/Teacher" className="link instrument cello">大提琴</Link>
        <ul className="navTeacherUl">
          {cello}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link to="/Teacher" className="link instrument piano">鋼琴</Link>
        <ul className="navTeacherUl">
          {piano}
        </ul>
      </li>

      <li className="instrumentLi">
        <Link to="/Teacher" className="link instrument flute">長笛</Link>
        <ul className="navTeacherUl">
          {flute}
        </ul>
      </li>

    </ul>
  );
};

export default NavTeacher;
