import { useState, useContext } from "react";
import { UserContext } from "../context";


function Teacher10() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div>
      <div>{teacher[9].name}</div>
      <div>{teacher[9].instrument}      
        <img 
          id='base64image' 
          className="base64image"
          src={'data:image/jpeg;base64,' + teacher[9].image}
          style={{"width": "200px"}}
          onClick={() => {
            setBigImg(true);
        }}/>
      </div>
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
          className="bigImg"
          src={'data:image/jpeg;base64,' + teacher[9].image}
          />
          <div className="close" 
            onClick={() => {
            setBigImg(false);
            }}>
              X
          </div>
        </div>
         : <></>
      }
      <div>{teacher[9].introduction}</div>
    </div>
  );
};

export default Teacher10;
