function ContactUs() {
  const contactUsText = () => {
    fetch("/api/小提琴")
    .then((res) => res.json())
    .then((data) => {
      setTeacher(data);
      setState({isLoading: false,});
    });
  };

  return (
    <div className="contactUs">
      <form className="contactUsForm" action="/api/contactUs" method="post">
        <div>
        <p>*</p><label>姓名：</label>
        <input type="text" name="firstName" placeholder="名字" required={true}></input>
        <input type="text" name="lastName" placeholder="姓氏" required={true}></input>
        </div>

        <div>
        <p>*</p><label>Email:</label>
        <input type="email" name="email" placeholder="電子信箱" required={true}></input>          
        </div>

        <div>
        <p>*</p><label>電話:</label>
        <input type="tel" name="phone" placeholder="電話" required={true}></input>          
        </div>

        <div>
        <p>*</p><label>內容:</label>
        <textarea name="reqText" placeholder="內容..." required={true}></textarea>          
        </div>
        <div>

        <input type="hidden" name="reqCode" readOnly={true} value="requireUs"></input>
        </div>

        <div>
          <input type="submit" name="submit" value="提交" onClick={contactUsText}></input>
        </div>

      </form>
    </div>
  );
};

export default ContactUs;
