import { useState } from "react";


function ContactUs() {
  const [ value, setValue ] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    reqText: "",
    reqCode: false,
  });
  const contactUsText = () => {
    setValue((prev) => {return {...prev, reqCode: true}})
    fetch("/api/contactUs", {
      method: "POST",
      body: JSON.stringify({
        firstName: value.firstName,
        lastName: value.lastName,
        email: value.email,
        phone: value.phone,
        reqText: value.reqText,
        reqCode: value.reqCode
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })

    .then((res) => res.json())
    .then((data) => {
      console.log("data", data)
      alert("已送出，謝謝您的聯繫。")
    })
  };
// e.target.value


  return (
    <div className="contactUs">
      {/* <form className="contactUsForm" action="/api/contactUs" method="post"> */}
      <form className="contactUsForm">
        <div>
          <p>*</p><label>姓名：</label>
          <input type="text" name="firstName" placeholder="名字" required={true} onChange={
            (e) => setValue((prev) => {
              return {...prev, firstName: e.target.value}
          })}>

          </input>
          <input type="text" name="lastName" placeholder="姓氏" required={true} onChange={
            (e) => setValue((prev) => {
              return {...prev, lastName: e.target.value}
          })}>

          </input>
        </div>

        <div>
          <p>*</p><label>Email:</label>
          <input type="email" name="email" placeholder="電子信箱" required={true} onChange={
            (e) => setValue((prev) => {
              return {...prev, email: e.target.value}
          })}>
          </input>          
        </div>

        <div>
          <p>*</p><label>電話:</label>
          <input type="tel" name="phone" placeholder="電話" required={true} onChange={
            (e) => setValue((prev) => {
              return {...prev, phone: e.target.value}
          })}>
          </input>          
        </div>

        <div>
          <p>*</p><label>內容:</label>
          <textarea name="reqText" placeholder="內容..." required={true} onChange={
            (e) => setValue((prev) => {
              return {...prev, reqText: e.target.value}
          })}>
          </textarea>          
        </div>
        {/* <div>

        <input type="hidden" name="reqCode" readOnly={true} value={value.reqCode}></input>
        </div> */}

        <div>
          <input type="submit" name="submit" value="提交" onClick={contactUsText}></input>
        </div>

      </form>
    </div>
  );
};

export default ContactUs;
