import { Link } from "react-router-dom";
import { useContext } from "react";
import { UserContext } from "../context";

function NavTeacher() {
  const instrumentValue = [];
  const teacher = useContext(UserContext);

  const Value = teacher.find((data) => {
    if (instrumentValue.indexOf(data.instrument) === -1) {
      instrumentValue.push(data.instrument);
      return instrumentValue;
    }
    return false;
  });
  console.log(Value);
  const instrument = instrumentValue.map((data, index) => {
    return (
    <li key={index} className="instrumentLi">
      <Link className="link instrument">{data}</Link>
      <ul className="navTeacherUl">
        {teacher.map((value, valueIndex) => {
          if (value.instrument === data) {
            return (
              <li key={valueIndex}>
                <Link to={`/${value._id}`} className="link navTeacher">{value.name}</Link>
              </li>
            );
          };
          return [];
        })}
      </ul>
    </li>
    );
  });

  return (
    <ul className="instrumentUl">
      {instrument}
    </ul>
  );
};

export default NavTeacher;
