import { useState } from "react";

function InquireEdit() {

  let level = [ "一年級", "二年級", "三年級", "四年級", "五年級", "六年級" ];
  const gradeMap = level.map((data, index) => {
    if (value.grade === "國小") {
      return <option key={index} name="graded" value={data}>{data}</option>
    }else if ((value.grade === "國中" && index < 3) || (value.grade === "高中" && index < 3)) {
      return <option key={index} name="graded" value={data}>{data}</option>
    }else if ((value.grade === "大學" && index < 4 )|| (value.grade === "高中" && index < 3)) {
      return <option key={index} name="graded" value={data}>{data}</option>
    }else return null
  });

  const [ id, setId ] = useState("");
  const [ state, setState ] = useState("");
  const [ isWriting, setIsWriting ] = useState(true);
  const [ inquire, setInquire ] = useState({
    date: "",
    name: "",
    email: "",
    birthday: "",
    addressCounty: "",
    addressDistrict: "",
    zipCode: "",
    address: "",
    id: "",
    roman: "",
    school: "",
    grade: "",
    graded: "",
    phone: "",
    fatherName: "",
    fatherPhone: "",
    matherName: "",
    matherPhone: "",
    studentPhone: "",
    introducer: "",
    instrument: "",
    experience: "",
    track: "",
    specify: "",
    friend1: "",
    friend2: "",
    friend3: "",
    diseaseVerify: "",
    disease: "",
    food: "",
    height: "",
    weight: "",
    TShirtSize: "",
  });
console.log(state)
  const inquireText = async () => {
    if (id === "" || id.length < 10) return alert("身分證錯誤");
    fetch("/api/signUp/inquire/" + id)
    .then((res) => {return res.json()})
    .then((data) => {
      setState(data[0]);
    })
    .catch((err) => console.log("error:", err));
  }
  const idText = (e) => {
    setId(e.target.value)
  }
  // console.log(state)
  // console.log(inquire)

  // const inquireForm = (
  //   <from>
  //     <div>{state[0].date}</div>
  //   </from>
  // )

  const reReadOnly = (e) => {
    console.log(e.target.id);
    setIsWriting(!isWriting);
  }

  const reword = (e) => {
    setState((prev) => {
      return {...prev, title: e.target.value}
    });
  }
  console.log("isWriting",isWriting);

  return (
    <div className="inquireEdit">
      <h2><i className="bi bi-search"></i> 查詢報名資料</h2>
      <form className="inquireEditForm">

      <label><h4>身分證字號: </h4></label>
      <input type="text" name="id" maxLength="10" value={id} onChange={(e) => idText(e)} required={true} />

      <input className="inputText" type="submit" name="submit" value="提交" onClick={inquireText} />
      {
        state === "" ?
        <div></div>
        :
        // inquireForm
        <div>
          <input type="button" id="name" name="nameButton" value="更改" onClick={reReadOnly} /><label><h5>申請日期: </h5></label>
          <div type="text" disabled={true} >{state.date}</div>

          <div>
            姓名:
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.name} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, name: e.target.value}
                });
            }} />

          </div>

          <div>
            性別: <input type="text" disabled={true} value={state.gender}  />
          </div> 

          <div>
            Email: 
              <input 
                type="text" 
                disabled={isWriting} 
                value={state.email} 
                onChange={(e) => {
                  setState((prev) => {
                    return {...prev, email: e.target.value}
                  });
              }} />
          </div>

          <div>
            生日: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.birthday} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, birthday: e.target.value}
                });
            }} />
          </div>

          <div>
            地址: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.address} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, address: e.target.value}
                });
            }} />
          </div>

          <div>
            身分證字號: 
            <input type="text" disabled={true} value={state.id}  />
          </div> 

          <div>
            護照拼音: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.roman} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, roman: e.target.value}
                });
            }} />
          </div>

          <div>
            就讀學校: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.school} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, school: e.target.value}
                });
            }} />
          </div>

          <div>
            年級: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.grade} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, grade: e.target.value}
                });
            }} />
          </div>

          <div>
            年級: 
            <select 
                name="grade"
                required={true} 
                onChange={(e) => {

                  setValue((prev) => {
                    return {...prev, grade: e.target.value}
                  })
                }}>
            <option name="grade" value="">請選擇</option>
            <option name="grade" value="國小">國小</option>
            <option name="grade" value="國中">國中</option>
            <option name="grade" value="高中">高中</option>
            <option name="grade" value="大學">大學</option>
          </select>
          <select 
                name="graded"
                required={true} 
                onChange={(e) => {

                  setValue((prev) => {
                    return {...prev, graded: e.target.value}
                  })
                }}>
            <option name="graded" value="">請選擇</option>
            {gradeMap}
          </select>
          </div>


        </div>
      }

      </form>
    </div>
  );
};

export default InquireEdit;
