import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher14() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div className="teacherCard">
      <div className="smImgDiv"> 
        <img 
          id='base64image' 
          className="base64image"
          src={'data:image/jpeg;base64,' + teacher[13].image}
          style={{"width": "200px"}}
          onClick={() => {
            setBigImg(true);
        }}/>
      </div> 
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
            className="bigImg"
            src={'data:image/jpeg;base64,' + teacher[13].image}
          />
          <div className="close" 
            onClick={() => {
            setBigImg(false);
          }}>
            X
          </div>
        </div>
         : <></>
      }
      <div>{teacher[13].introduction}</div>
    </div>
  );
};

export default Teacher14;
