import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher1() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div className="teacherCard">
      <div className="smImgDiv">
        <img 
          id='base64image' 
          className="base64image"
          src={'data:image/jpeg;base64,' + teacher[0].image}
          style={{"width": "200px"}}
          onClick={() => {
            setBigImg(true);
          }}
        />
      </div>
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
          className="bigImg"
          src={'data:image/jpeg;base64,' + teacher[0].image}
          />
          <div className="close" 
            onClick={() => {
            setBigImg(false);
            }}>
              X
          </div>
        </div>
         : <></>
      }
      <div className="teacherMessage"></div>
        <h5 className="teacherName">{teacher[0].name}</h5>
        <h5 className="teacherInstrument">{teacher[0].instrument}</h5>
        <p className="teacherIntroduction">{teacher[0].introduction}</p>

    </div>
  );
};

export default Teacher1;
