import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher1() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div>
      <div>{teacher[0].name}</div>
      <div>{teacher[0].instrument}</div>
      <img id='base64image'
       src={'data:image/jpeg;base64,' + teacher[0].image}
       style={{"width": "200px"}}
      />
      <div>{teacher[0].introduction}</div>
    </div>
  );
};

export default Teacher1;
