// import  'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import HeaderApp from "./AppHTML/HeaderApp";
import NavApp from "./AppHTML/NavApp"; 
import MainApp from "./AppHTML/MainApp";
import FooterApp from "./AppHTML/FooterApp";


import React, { useState, useEffect } from "react";
import { ReactComponent as LoadingIcon } from "./images/loading.svg";
import { HashRouter } from "react-router-dom";
import { UserContext } from "./context";
// import { HashRouter, Routes, Route } from "react-router-dom";



function App() {
  const [ teacher, setTeacher ] = useState([]);
  const [ state, setState ] = useState({
    isLoading: true,
  });
  // GET teacher database-----------------
  useEffect(() => {
    if (state.isLoading) {
    fetch("/api/小提琴")
    .then((res) => res.json())
    .then((data) => {
      setTeacher(data);
      setState({isLoading: false,});
    });
    };
  }, []);
  //----------------------------------

  let x = [];
    Promise.all(
    teacher.map(async (data) => {
      x.push(data);
      // return data.name
    })
    )
  //   .then((results) => {
  //   return results;
  // });



  return (
    <UserContext.Provider value={x}>
      {console.log("Loading is ", state.isLoading)}
      <div className="redoIconContainer">
        {state.isLoading ? <RedoIcon className="redoIcon"  /> : <></>}
      </div>
      {state.isLoading ? <></> : 
        <div className="gridContainer">
          <HeaderApp />
          <HashRouter>
            <NavApp />
            <MainApp/>
          </HashRouter>
          <FooterApp />
        </div>
      }
    </UserContext.Provider>


  );
}


export default App;
