import { useState } from "react";

function InquireEdit() {

  const [ id, setId ] = useState("");
  const [ state, setState ] = useState("");
  const [ isWriting, setIsWriting ] = useState(true);
  const [ inquire, setInquire ] = useState({
    date: "",
    name: "",
    email: "",
    birthday: "",
    addressCounty: "",
    addressDistrict: "",
    zipCode: "",
    address: "",
    id: "",
    roman: "",
    school: "",
    grade: "",
    graded: "",
    phone: "",
    fatherName: "",
    fatherPhone: "",
    matherName: "",
    matherPhone: "",
    studentPhone: "",
    introducer: "",
    instrument: "",
    experience: "",
    track: "",
    specify: "",
    friend1: "",
    friend2: "",
    friend3: "",
    diseaseVerify: "",
    disease: "",
    food: "",
    height: "",
    weight: "",
    TShirtSize: "",
  });
console.log(state)
  const inquireText = async () => {
    if (id === "" || id.length < 10) return alert("身分證錯誤");
    fetch("/api/signUp/inquire/" + id)
    .then((res) => {return res.json()})
    .then((data) => {
      setState(data[0]);
    })
    .catch((err) => console.log("error:", err));
  }
  const idText = (e) => {
    setId(e.target.value)
  }
  // console.log(state)
  // console.log(inquire)

  // const inquireForm = (
  //   <from>
  //     <div>{state[0].date}</div>
  //   </from>
  // )

  const reReadOnly = (e) => {
    console.log(e.target.id);
    setIsWriting(!isWriting);
  }

  const reword = (e) => {
    setState((prev) => {
      return {...prev, title: e.target.value}
    });
  }
  console.log("isWriting",isWriting);

  return (
    <div className="inquireEdit">
      <h2><i className="bi bi-search"></i> 查詢報名資料</h2>
      <form className="inquireEditForm">

      <label><h4>身分證字號: </h4></label>
      <input type="text" name="id" maxLength="10" value={id} onChange={(e) => idText(e)} required={true} />

      <input className="inputText" type="submit" name="submit" value="提交" onClick={inquireText} />
      {
        state === "" ?
        <div></div>
        :
        // inquireForm
        <div>
          <input type="button" id="name" name="nameButton" value="更改" onClick={reReadOnly} />

            <div type="text" disabled={true} >申請年份: {state.reqCode}</div>
            <div type="text" disabled={true} >填單日期: {state.date}</div>

          <div>
            姓名:
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.name} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, name: e.target.value}
                });
            }} />
          </div>

          <div>
            性別: <input type="text" disabled={true} value={state.gender}  />
          </div> 

          <div>
            Email: 
              <input 
                type="text" 
                disabled={isWriting} 
                value={state.email} 
                onChange={(e) => {
                  setState((prev) => {
                    return {...prev, email: e.target.value}
                  });
              }} />
          </div>

          <div>
            生日: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.birthday} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, birthday: e.target.value}
                });
            }} />
          </div>

          <div>
            地址: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.address} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, address: e.target.value}
                });
            }} />
          </div>

          <div>
            身分證字號: 
            <input type="text" disabled={true} value={state.id}  />
          </div> 

          <div>
            護照拼音: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.roman} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, roman: e.target.value}
                });
            }} />
          </div>

          <div>
            就讀學校: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.school} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, school: e.target.value}
                });
            }} />
          </div>

          <div>
            年級: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.grade} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, grade: e.target.value}
                });
            }} />
          </div>

          <div>
            家裡電話: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.phone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, phone: e.target.value}
                });
            }} />
          </div>

          <div>
            父親姓名: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.fatherName} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, fatherName: e.target.value}
                });
            }} />
          </div>

          <div>
            父親手機: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.fatherPhone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, fatherPhone: e.target.value}
                });
            }} />
          </div>

          <div>
            母親姓名: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.matherName} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, matherName: e.target.value}
                });
            }} />
          </div>

          <div>
            母親手機: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.matherPhone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, matherPhone: e.target.value}
                });
            }} />
          </div>

          <div>
            學生手機: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.studentPhone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, studentPhone: e.target.value}
                });
            }} />
          </div>

          <div>
            介紹人: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.introducer} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, introducer: e.target.value}
                });
            }} />
          </div>

          <div>
            參加項目: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.instrument} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, instrument: e.target.value}
                });
            }} />
          </div>

          <div>
            學習經驗: 
            <input 
              type="text" 
              disabled={isWriting} 
              value={state.instrument} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, instrument: e.target.value}
                });
            }} />
          </div>


        </div>
      }

      </form>
    </div>
  );
};

