import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher4() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div>

      <img 
        id='base64image' 
        className="base64image"
        src={'data:image/jpeg;base64,' + teacher[3].image}
        style={{"width": "200px"}}
        onClick={() => {
          setBigImg(true);
        }}
      />
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
          className="bigImg"
          src={'data:image/jpeg;base64,' + teacher[3].image}
          />
          <div className="close" 
            onClick={() => {
            setBigImg(false);
            }}>
              X
          </div>
        </div>
         : <></>
      }
      <div className="teacherMessage">
        <h5 className="teacherName">{teacher[3].name}</h5>
        <h5 className="teacherInstrument">{teacher[3].instrument}</h5>
        <p className="teacherIntroduction">{teacher[3].introduction}</p>
      </div>
    </div>
  );
};

export default Teacher4;
