  const yyyy = today.getFullYear();
  const mm = String(today.getMonth() + 1).padStart(2, '0');
  const dd = String(today.getDate()).padStart(2, '0');
  today = yyyy + "-" + mm + "-" + dd;

  const [ id, setId ] = useState("");
  const [ state, setState ] = useState("");
  const [ isWriting, setIsWriting ] = useState(true);
  const [ inquire, setInquire ] = useState({
    date: "",
    name: "",
    gender: state.gender === "男" ? true : false,
    email: "",
    birthday: "",
    addressCounty: "",
    addressDistrict: "",
    zipCode: "",
    address: "",
    id: "",
    roman: "",
    school: "",
    grade: "",
    graded: "",
    phone: "",
    fatherName: "",
    fatherPhone: "",
    matherName: "",
    matherPhone: "",
    studentPhone: "",
    introducer: "",
    instrument: "",
    experience: "",
    track: "",
    specify: "",
    friend1: "",
    friend2: "",
    friend3: "",
    diseaseVerify: "",
    disease: "",
    food: "",
    height: "",
    weight: "",
    TShirtSize: "",
  });

  const gender = async () => {
    console.log(inquire.gender);
    setState((prev) => {
      return { 
        ...prev, 
        gender: !inquire.gender,
      };
    });
};

  const inquireText = async () => {
    if (id === "" || id.length < 10) {
      return alert("身分證錯誤")
    };
    fetch("/api/signUp/inquire/" + id)
    .then((res) => {return res.json()})
    .then((data) => {
      if (data === "無資料") return alert(data);
      alert("已送出");

      setState(data[0]);
    })
    .catch((err) => console.log("error:", err));
  }

  const outputText = async () => {
    fetch("/api/update/" + id, {
      method: "PUT",
      body: JSON.stringify({
        state: state
      }),
      headers: {
        'Content-Type': 'application/json'
      },
    })
    .then((res) => {return res.json()})
    .then((data) => {
      alert("已送出", data);
      console.log(data);
    })
    .catch((err) => console.log("error:", err));
  };

  const idText = (e) => {
    setId(e.target.value)
  }

  const reReadOnly = (e) => {
    setIsWriting(!isWriting);
  }


  return (
    <div className="inquireEdit">
      <h2><i className="bi bi-search"></i> 查詢報名資料</h2>
      <form className="inquireEditForm">

      <div className="checkDB">
        <label><h4>身分證字號： </h4></label>
        <input type="text" name="id" maxLength="10" size="10" value={id} onChange={(e) => idText(e)} required={true} />
      </div>


      <input className="inputText" type="button" name="submit" value="提交" onClick={inquireText} />
      {
        state === "" ?
        <div></div>
        :
        // inquireForm
        <div>
          <input className="inputText changeButton" type="button" id="name" name="changeButton" value="更改" onClick={reReadOnly} />

            <div type="text" disabled={true} >申請年份： {state.reqCode}</div>
            <div type="text" disabled={true} >填單日期： {state.date}</div>

          <div>
            姓名：
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.name} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, name: e.target.value}
                });
            }} />
          </div>

          <div>
            {/* 性別： <input className="notChangeInput" type="text" disabled={true} value={state.gender}  /> */}
            <div className="gender">
              <input 
                className="notChangeInput"
                type="radio" 
                id="Male" 
                name="gender"
                disabled={isWriting}
                value="男"
                onChange={gender} 
                checked={inquire.gender} 
              />
              <label className="gender" htmlFor="Male">男</label>
              <input 
                className="notChangeInput"
                type="radio" 
                id="Female" 
                name="gender"
                disabled={isWriting}
                value="女" 
                onChange={gender} 
                checked={!inquire.gender} 
              />
              <label className="gender" htmlFor="Female">女</label>
            </div>
          </div> 

          <div>
            Email： 
              <input 
                type="text" 
                className="changeInput"
                disabled={isWriting} 
                value={state.email} 
                onChange={(e) => {
                  setState((prev) => {
                    return {...prev, email: e.target.value}
                  });
              }} />
          </div>

          <div>
            生日： 
            <input 
              className="changeInput" 
              disabled={isWriting} 
              value={state.birthday} 
              type="date" 
              name="birthday" 
              max={today}
              onChange={(e) => setState((prev) => {
                return {...prev, birthday: e.target.value}
            })} />
          </div>

          <div>
            地址： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.address} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, address: e.target.value}
                });
            }} />
          </div>

          <div>
            身分證字號： 
            <input className="notChangeInput" type="text" disabled={true} value={state.id}  />
          </div> 

          <div>
            護照拼音： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.roman} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, roman: e.target.value}
                });
            }} />
          </div>

          <div>
            就讀學校： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.school} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, school: e.target.value}
                });
            }} />
          </div>

          <div>
            年級： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.grade} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, grade: e.target.value}
                });
            }} />
          </div>

          <div>
            家裡電話： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.phone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, phone: e.target.value}
                });
            }} />
          </div>

          <div>
            父親姓名： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.fatherName} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, fatherName: e.target.value}
                });
            }} />
          </div>

          <div>
            父親手機： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.fatherPhone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, fatherPhone: e.target.value}
                });
            }} />
          </div>

          <div>
            母親姓名： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.matherName} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, matherName: e.target.value}
                });
            }} />
          </div>

          <div>
            母親手機： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.matherPhone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, matherPhone: e.target.value}
                });
            }} />
          </div>

          <div>
            學生手機： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.studentPhone} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, studentPhone: e.target.value}
                });
            }} />
          </div>

          <div>
            介紹人： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.introducer} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, introducer: e.target.value}
                });
            }} />
          </div>

          <div>
            參加項目： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.instrument} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, instrument: e.target.value}
                });
            }} />
          </div>

          <div>
            學習經驗： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.experience} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, experience: e.target.value}
                });
            }} />
          </div>

          <div>
            目前學的曲目： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.track} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, track: e.target.value}
                });
            }} />
          </div>

          <div>
            指定個別課教授： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.specify} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, specify: e.target.value}
                });
            }} />
          </div>

          <div>
            我想和好朋友住：
          </div>            
          1. <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.friend1} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, friend1: e.target.value}
                });
            }} />
          2. <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.friend2} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, friend2: e.target.value}
                });
            }} />
          3. <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.friend3} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, friend3: e.target.value}
                });
            }} />

          <div>
            身高： 
            <input 
              type="text" 
              className="changeInput"
              disabled={isWriting} 
              value={state.height} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, height: e.target.value}
                });
            }} />
          </div>

          <div>
            體重： 
            <input 
              type="text"
              className="changeInput"
              disabled={isWriting} 
              value={state.weight} 
              onChange={(e) => {
                setState((prev) => {
                  return {...prev, weight: e.target.value}
                });
            }} />
          </div>

        <input className="inputText" type="button" name="submit" value="提交" onClick={outputText} />
        </div>

      }


      </form>
    </div>
  );
};

export default InquireEdit;
