// import  'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import HeaderApp from "./AppHTML/HeaderApp";
import NavApp from "./AppHTML/NavApp"; 
import MainApp from "./AppHTML/MainApp";
import FooterApp from "./AppHTML/FooterApp";

import React, { useState, useEffect, createContext, useContext } from "react";
import { HashRouter } from "react-router-dom";
const UserContext = createContext()
// import { HashRouter, Routes, Route } from "react-router-dom";



function App() {
  const [ teacher, setTeacher ] = useState([]);

  // GET teacher data-----------------
  useEffect(() => {
    fetch("/api/小提琴")
    .then((res) => res.json())
    .then((data) => setTeacher(data))
  }, [])
  //----------------------------------
  // const x = teacher.map(async (v, i) => {
  //   return v.name
  // })


  // const teacherName = await Promise.all(x).then(function(results) {
  //   // console.log(results)
  //   return results;
  // });

  let x = [];
    Promise.all(
    teacher.map(async (v, i) => {
      console.log(v.name)
      x.push(v);
      return v.name
    })
    )
    .then((results) => {
    console.log(results)
    return results;
  });

  console.log(x)

  return (
    <UserContext.Provider value={x}>
      <div className="gridContainer">
        <HeaderApp />
        <HashRouter>
          <NavApp />
          <MainApp/>
        </HashRouter>

        <FooterApp />
      </div>
    </UserContext.Provider>


  );
}

export default App;
