import { Routes, Route } from "react-router-dom";
import { useContext } from "react";
import { UserContext } from "../context";
import Home from "../JSX/Home";
import Teacher1 from "../Teacher/Teacher1";
import Teacher2 from "../Teacher/Teacher2";
import Teacher3 from "../Teacher/Teacher3";
import Teacher4 from "../Teacher/Teacher4";
import Teacher5 from "../Teacher/Teacher5";
import Teacher6 from "../Teacher/Teacher6";
import Teacher7 from "../Teacher/Teacher7";

function MainApp() {
  const teacher = useContext(UserContext);

  const teacherName = teacher.map((data, index) => data._id);
  return (
    <div className="mainApp">
      <Routes>      
        <Route exact path="/" element={<Home />} />
        <Route path={`/${teacherName[0]}`} element={<Teacher1 />} />
        <Route path={`/${teacherName[1]}`} element={<Teacher2 />} />
        <Route path={`/${teacherName[2]}`} element={<Teacher3 />} />
        <Route path={`/${teacherName[3]}`} element={<Teacher4 />} />
        <Route path={`/${teacherName[4]}`} element={<Teacher5 />} />
        <Route path={`/${teacherName[5]}`} element={<Teacher6 />} />
        <Route path={`/${teacherName[6]}`} element={<Teacher7 />} />
      </Routes>
    </div>
  );
};

export default MainApp;
