import { useState } from "react";

function InquireEdit() {
  const [ id, setId ] = useState("");
  const [ state, setState ] = useState("");
  const [ isWriting, setIsWriting ] = useState(true);
  const [ inquire, setInquire ] = useState({
    date: "",
    name: "",
    email: "",
    birthday: "",
    addressCounty: "",
    addressDistrict: "",
    zipCode: "",
    address: "",
    id: "",
    roman: "",
    school: "",
    grade: "",
    graded: "",
    phone: "",
    fatherName: "",
    fatherPhone: "",
    matherName: "",
    matherPhone: "",
    studentPhone: "",
    introducer: "",
    instrument: "",
    experience: "",
    track: "",
    specify: "",
    friend1: "",
    friend2: "",
    friend3: "",
    diseaseVerify: "",
    disease: "",
    food: "",
    height: "",
    weight: "",
    TShirtSize: "",
  });
console.log(state)
  const inquireText = async () => {
    if (id === "" || id.length < 10) return alert("身分證錯誤");
    fetch("/api/signUp/inquire/" + id)
    .then((res) => {return res.json()})
    .then((data) => {
      setState(data[0]);
    })
    .catch((err) => console.log("error:", err));
  }
  const idText = (e) => {
    setId(e.target.value)
  }
  // console.log(state)
  // console.log(inquire)

  // const inquireForm = (
  //   <from>
  //     <div>{state[0].date}</div>
  //   </from>
  // )

  const reReadOnly = (e) => {
    console.log(e.target.id);
    setIsWriting(!isWriting);
  }

  const reword = (e) => {
    console.log(e.target.value);
    setState((prev) => {
      return {...prev, name: e.target.value}
    });

  }
  console.log("isWriting",isWriting);

  return (
    <div className="inquireEdit">
      <h2><i className="bi bi-search"></i> 查詢報名資料</h2>
      <form className="inquireEditForm">

      <label><h4>身分證字號: </h4></label>
      <input type="text" name="id" maxLength="10" value={id} onChange={(e) => idText(e)} required={true} />

      <input className="inputText" type="submit" name="submit" value="提交" onClick={inquireText} />
      {
        state === "" ?
        <div></div>
        :
        // inquireForm
        <div>
          <label><h5>申請日期: </h5></label>
          {/* <div type="text" readOnly={true} >{state[0].date}</div> */}
          <div>
            姓名: <input type="text" readOnly={isWriting} value={state.name} onChange={reword} />
            <input type="button" disabled={false} id="name" name="nameButton" value="更改" onClick={reReadOnly} />
          </div>
          <div>
            性別: <input type="text" value={state.gender}  />
          </div> 
        </div>


      }

      </form>
    </div>
  );
};

export default InquireEdit;
