function Plan() {

  const starText = [
    "四人套房。", 
    "腹地優美，活動場域設備加級完善，讓學員放心安靜休息。", 
    "每日巡房，隨時注意學員安全，加強營區全面之消毒與環境衛生。"
  ];

  const starLi = starText.map((data, index) => {
    return (
        <li key={"starLi" + index}>{data}</li>
    );
  });

  const text = [
    "為了落實健康生活， 本次活動將全程宣導學員勤洗手。", 
    "活動期間備有消毒酒精、洗手用品供學員使用。", 
    "音樂營期間實行每日體溫量測。"
  ];

  const li = text.map((data, index) => {
    return (
        <li key={"li" + index}>{data}</li>
    );
  });

  return (
    <div className="plan">

      <ul>
        {starLi}
      </ul>

      <div>
        <div>筋骨活暢&emsp;&emsp;&emsp;沉淨於渡假村生態活動， 讓學員舒展身心運動的時間。</div>
        <div>腹地優美，活動場域設備加級完善，讓學員放心安靜休息。</div>
        <div>每日巡房，隨時注意學員安全，加強營區全面之消毒與環境衛生。</div>
      </div>

      <ul>
        {li}
      </ul>

    </div>
  );
};

export default Plan;
