import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher2() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div>
      <div>{teacher[1].name}</div>
      <div>{teacher[1].instrument}</div>
      <img 
        id='base64image' 
        className="base64image"
        src={'data:image/jpeg;base64,' + teacher[1].image}
        style={{"width": "200px"}}
        onClick={() => {
          console.log(bigImg);
          setBigImg(true);
        }}
      />
      <img 
        className="bigImg"
        src={'data:image/jpeg;base64,' + teacher[1].image}
       ></img>
       <div className="close">X</div>
      <div>{teacher[1].introduction}</div>
    </div>
  );
};

export default Teacher2;
