import { useState, useContext } from "react";
import { UserContext } from "../context";

function Teacher9() {
  const [ bigImg, setBigImg ] = useState(false);
  const teacher = useContext(UserContext);
  return (
    <div>
      <div>{teacher[8].name}</div>
      <div>{teacher[8].instrument}</div>      
      <img 
        id='base64image' 
        className="base64image"
        src={'data:image/jpeg;base64,' + teacher[8].image}
        style={{"width": "200px"}}
        onClick={() => {
          setBigImg(true);
        }}
      />
      {bigImg ? 
        <div className="bigImgDiv">
          <img 
          className="bigImg"
          src={'data:image/jpeg;base64,' + teacher[8].image}
          />
          <div className="close" 
            onClick={() => {
            setBigImg(false);
            }}>
              X
          </div>
        </div>
         : <></>
      }
      <div>{teacher[8].introduction}</div>
    </div>
  );
};

export default Teacher9;
